var title_f20_32_21004="Whole milk diets";
var content_f20_32_21004=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F68248&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F68248&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Whole milk diets for use during the rehabilitation phase of treatment for malnutrition",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tr>",
"          <td class=\"subtitle1_single\">",
"           To make the starter diet from milk:",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           Mix 50 g sugar and 10 mL vegetable oil together thoroughly.",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           Add 150 mL boiled whole (full cream) milk and 10 mL of the electrolyte solution (see paragraph 3) if used.",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           Make up to 500 mL with boiled water. Whisk to stop oil separating out or mix vigorously before serving. This feed keeps up to 12 hours. Make more when necessary.",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           In the evening make sure there is enough for the night feeds.",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           Feed at a rate of 130 mL/kg body weight/24 hours. Divide into eight feeds and feed every three hours.",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           A child who is breastfed should be encouraged to suckle after drinking the milk feed.",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           <strong>",
"            Never add salt,",
"           </strong>",
"           explain to the parent that the child needs sweet not salty food.",
"          </td>",
"         </tr>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tr>",
"          <td class=\"subtitle1_single\">",
"           To make a high energy high nutrient rehabilitation diet from milk:",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           Mix 40 g sugar and 10 mL vegetable oil.",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           Add 440 mL boiled whole milk and 10 mL of the electrolyte solution.",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           Make up to 500 mL with boiled water and whisk vigorously.",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           For two days, feed at same frequency and amount as the initial diet, then increase the amount by 10 mL/feed and reduce frequency of feeds to every four hours. Feed at a rate of approximately 200 mL/kg body weight/day (or more if child wants it). When given four hourly, each feed should be 166 mL.",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           If milk is difficult to get, make a thick cereal porridge and add oil or margarine or sugar and bean, pea or peanut flour, peanut butter or eggs. Start giving fruits and vegetables, particularly those rich in vitamin A.",
"          </td>",
"         </tr>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Improving child nutrition: Treating severe malnutrition. Child Health Dialogue 200; 19. Copyright &copy;2000 Healthlink.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
