var title_f20_32_21005="DDx blister pustule infant";
var content_f20_32_21005=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F75417&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F75417&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Vesiculopustular lesions in neonates and infants that require treatment and/or monitoring",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Condition",
"      </td>",
"      <td class=\"subtitle1\">",
"       Clinical features",
"      </td>",
"      <td class=\"subtitle1\">",
"       Significance",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       Infectious conditions",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Congenital herpes simplex virus (HSV)",
"      </td>",
"      <td>",
"       Grouped or single vesicles on erythematous base in crops on skin and mucous membranes; lesions usually appear between one and two weeks of age; may be associated with nonspecific signs of serious illness: temperature instability, respiratory distress, poor feeding, lethargy",
"      </td>",
"      <td>",
"       Requires antiviral therapy; significant morbidity and mortality if untreated",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Neonatal varicella zoster virus",
"      </td>",
"      <td>",
"       Grouped or single vesicles on erythematous base in crops on skin and mucous membranes",
"      </td>",
"      <td>",
"       Requires antiviral therapy; associated with significant morbidity and mortality",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Congenital syphilis",
"      </td>",
"      <td>",
"       Blisters, erosions that frequently involve the palms and soles; other manifestations include rhinitis, anemia, jaundice, hepatomegaly",
"      </td>",
"      <td>",
"       Requires antibiotic therapy; late manifestations in untreated infants may include central nervous system, skeletal, and dental abnormalities; hearing loss; and interstitial keratitis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Staphylococcal pustulosis",
"      </td>",
"      <td>",
"       Erythematous papules, pustules, honey-colored crusts often in areas of trauma",
"      </td>",
"      <td>",
"       Requires antibiotic therapy; gram stain and culture of lesions should be obtained; may be associated with systemic/invasive infection",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Staphylococcal scalded skin syndrome (SSSS)",
"      </td>",
"      <td>",
"       Fever, irritability, diffuse blanching erythema, flaccid blisters; positive Nikolsky sign*",
"      </td>",
"      <td>",
"       Requires antibiotic therapy; cultures should be obtained from any suspected focus of infection (eg, blood, urine, nasopharynx, umbilicus)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Streptococcal infections",
"      </td>",
"      <td>",
"       May mimic Staphylococcal infections",
"      </td>",
"      <td>",
"       Same as for Staphylococcal pustulosis and SSSS",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Listeria",
"      </td>",
"      <td>",
"       Pustules of the skin and mucus membranes may be present in early onset disease (&lt;7 days of age)",
"      </td>",
"      <td>",
"       Requires antibiotic therapy; may be associated with septicemia and/or meningitis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Candidiasis",
"      </td>",
"      <td>",
"       Erythematous macules and papules evolving to pustules and vesicles",
"      </td>",
"      <td>",
"       Requires antifungal therapy; has the potential to disseminate via the bloodstream in susceptible hosts",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       Congenital disorders",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Epidermolysis bullosa",
"      </td>",
"      <td>",
"       Blister development with little or no trauma",
"      </td>",
"      <td>",
"       Management involves prevention of trauma, careful wound care, and treatment of infection",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Epidermolytic hyperkeratosis",
"      </td>",
"      <td>",
"       Widespread blistering and erythema and or hyperkeratosis; denuded areas of skin",
"      </td>",
"      <td>",
"       Increased susceptibility to cutaneous infection",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Aplasia cutis congenita",
"      </td>",
"      <td>",
"       Erosions present at birth that reepitheliaze to form hypertrophic or atrophic scar; involves the epidermis and dermis",
"      </td>",
"      <td>",
"       May be associated with other disorders (eg, trisomy 13, 4p-)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Incontinentia pigmenti",
"      </td>",
"      <td>",
"       Four stages that may occur simultaneously: linear streaks of erythematous papules and vesicles; warty papules or plaques in linear or swirling patterns; swirled; hypopigmented patches or streaks",
"      </td>",
"      <td>",
"       Majority of cases associated with neurologic, ocular, dental, and structural abnormalities",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       Miscellaneous",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Scabies",
"      </td>",
"      <td>",
"       May be seen in infants as young as 3 to 4 weeks of age, but never present at birth; vesicles, pustules, and papules, rare burrows on hands, feet, trunk, genitalia",
"      </td>",
"      <td>",
"       Requires treatment with scabicide and measures to prevent spread",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Cutaneous mastocytosis",
"      </td>",
"      <td>",
"       Bullous eruptions with hemorrhage; positive Darier sign&bull;",
"      </td>",
"      <td>",
"       Requires symptomatic therapy and avoidance of triggers",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Nikolsky sign: separation of the upper dermis and wrinkling of the skin with application of gentle pressure.",
"     <br>",
"      &bull; Darier sign: urticaria and erythema with rubbing, scratching, or stroking.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
