" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Abacavir hypersensitivity reaction",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/35/21049/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/35/21049/contributors\">",
"     Elizabeth J Phillips, MD, FRCPC, FRACP",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/35/21049/contributors\">",
"     Simon A Mallal, MBBS, FRACP, FRCPA",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/35/21049/contributors\">",
"     Section Editor",
