var title_f20_36_21068="Symptoms and signs of anaphylaxis";
var content_f20_36_21068=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F66333&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F66333&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Symptoms and signs of anaphylaxis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Skin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Feeling of warmth, flushing (erythema), itching (may occur in areas, such as external auditory canals, palms, soles, or groin), urticaria, angioedema, morbilliform rash, and \"hair standing on end\" (pilor erection)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Oral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Itching or tingling of lips, tongue, or palate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Edema of lips, tongue, uvula, metallic taste",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Respiratory",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Nose - itching, congestion, rhinorrhea, and sneezing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Laryngeal - itching and \"tightness\" in the throat, dysphonia, hoarseness, stridor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lower airways - shortness of breath (dyspnea), chest tightness, deep or repetitive cough, wheezing, and cyanosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Gastrointestinal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Nausea, abdominal pain (colic, cramps), vomiting (large amounts of \"stringy\" mucus), diarrhea, and dysphagia (difficulty swallowing*)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Cardiovascular",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Feeling of faintness or dizziness; syncope, altered mental status, chest pain, palpitations, tachycardia, bradycardia or other dysrhythmia, hypotension, tunnel vision, difficulty hearing, urinary or fecal incontinence, and cardiac arrest",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Neurologic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anxiety, apprehension, sense of impending doom, seizures, headache",
"        <sup>",
"         &bull;",
"        </sup>",
"        , and confusion; children may become irritable, cease to play, or have other sudden behavioral changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Ocular",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Periorbital itching, erythema and edema, tearing, and conjunctival erythema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Other",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Uterine cramps and bleeding in women and girls",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Often occurs in association with throat tightness and other upper airway symptoms.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Not common in anaphylaxis overall; however, reported in up to 30 percent of patients with exercise-induced anaphylaxis.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
