" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Regulation of iron balance",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/37/21082/contributors\">",
