var content_f20_4_20557=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F71435&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F71435&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Rome III diagnostic criteria for functional gastrointestinal disorders of childhood (ages 4 to 18 years)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Functional dyspepsia (must include all of the following):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Within the preceding two months, at least weekly occurrence of:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Persistent or recurring pain or discomfort in the upper abdomen,",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        No evidence of inflammatory, anatomic, metabolic, or neoplastic process to explain the symptoms,",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pain or discomfort not relieved by defecation or associated with the onset of a change in stool frequency or form",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Irritable bowel syndrome (must include all of the following):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Within the preceding two months, at least weekly occurrence of:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Abdominal discomfort or pain associated with &ge;2 of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Relieved with defecation,",
"        <strong>",
"         and/or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Onset associated with a change in frequency of stool,",
"        <strong>",
"         and/or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Onset associated with a change in form (appearance) of stool,",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        No evidence of inflammatory, anatomic, metabolic, or neoplastic process to explain the symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Functional abdominal pain (must include all of the following):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Within the preceding two months, at least weekly occurrence of:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Episodic or continuous abdominal pain,",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Insufficient criteria for other functional gastrointestinal disorders,",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        No evidence of inflammatory, anatomic, metabolic, or neoplastic process to explain the symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Childhood functional abdominal pain syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Within the preceding two months, at least weekly occurrence of:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Childhood functional abdominal pain at least 25 percent of the time and &ge;1 of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Some loss of daily functioning,",
"        <strong>",
"         and/or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Additional somatic symptoms such as headache, limb pain, or difficulty sleeping",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Abdominal migraine pain (must include all of the following):",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Within the preceding 12 months, &ge;2 episodes of:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Paroxysmal episodes of intense, acute, periumbilical pain that lasts for &ge;1 hour,",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Intervening periods of usual health lasting weeks to months,",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        The pain interferes with normal activities,",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        The pain is associated with &ge;2 of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Anorexia,",
"        <strong>",
"         and/or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Nausea,",
"        <strong>",
"         and/or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Headache,",
"        <strong>",
"         and/or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Photophobia,",
"        <strong>",
"         and/or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Pallor,",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        No evidence of inflammatory, anatomic, metabolic, or neoplastic process to explain the symptoms",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from: Rasquin A, Di Lorenzo C, Forbes D, et al. Childhood functional gastrointestinal disorders: child/adolescent. Gastroenterology 2006; 130:1527. Copyright &copy;2006 Elsevier.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
