" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 535px\">",
"   <div class=\"ttl\">",
"    Toxoplasma chorioretinitis",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 515px; height: 385px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
