var title_f20_40_21132="Predict noncompliance CPAP";
var content_f20_40_21132=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F50144&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F50144&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    General predictors of noncompliance with nasal CPAP",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tr>",
"      <td class=\"container\">",
"       <table cellspacing=\"0\">",
"        <tr>",
"         <td class=\"subtitle1_single\">",
"          Patient related factors",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Failure to understand the importance of the therapy.",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Failure to understand instructions concerning the therapy.",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Concomitant self-administration of prescription or non-prescription medications or alcohol.",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Social Isolation, thus lack of social support. (Patients with supportive families have been shown to be more compliant with prescription drugs - data not available for CPAP use).",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Feeling ill, or being too tired to use the therapy.",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Physical limitations, including vision, hearing, hand coordination.",
"         </td>",
"        </tr>",
"       </table>",
"      </td>",
"      <td class=\"container\">",
"       <table cellspacing=\"0\">",
"        <tr>",
"         <td class=\"subtitle1_single\">",
"          Therapy and medication related factors",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Complexity of therapy, in device use, or medication dosing.",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Increased rate of adverse reactions. (Device use has complications, and the provider needs to meet with the patient periodically to determine adverse events and help address these issues).",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Characteristics of illness; long-term or chronic illnesses are a problem, as compliance decreases over time.",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Expensive therapy (Only a problem when a patient must pay out of pocket or has not met the deductible.)",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Lack of efficacy.",
"         </td>",
"        </tr>",
"       </table>",
"      </td>",
"      <td class=\"container\">",
"       <table cellspacing=\"0\">",
"        <tr>",
"         <td class=\"subtitle1_single\">",
"          Health professional related factors",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Poor relationship with patient.",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Expression of doubt concerning therapeutic potential.",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Unwillingness to educate patients.",
"         </td>",
"        </tr>",
"        <tr>",
"         <td>",
"          Lack of knowledge of medications the patient is taking or has access to. (Sedatives and alcohol can compound OSA, and their use should be evaluated).",
"         </td>",
"        </tr>",
"       </table>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
