var title_f20_41_21147="Assessment for auto-HCT eligibility";
var content_f20_41_21147=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F63946&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F63946&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Assessment for autologous hematopoietic cell transplantation eligibility",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Detailed history and physical examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Including Karnofsky performance status and dental examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Laboratory studies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Complete blood count with differential; chemistries with liver and renal function and electrolytes; pregnancy test as clinically indicated; serology for HIV, hepatitis B, hepatitis C, herpes simplex and varicella, and cytomegalovirus; urine for microanalysis and macroanalysis; ABO typing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bone marrow aspiration and biopsy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Imaging",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Chest radiograph, an electrocardiogram, and a study of cardiac function (eg, ejection fraction measured by echocardiogram or MUGA)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary function test, including DLCO",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Disease-specific restaging studies",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     MUGA: multiple gated cardiac blood pool imaging; DLCO: diffusing capacity of the lungs for carbon monoxide.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
