" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Diagnosis and treatment of murine typhus",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/46/21219/contributors\">",
