var title_f20_46_21228="Oral antibiotics for suspected cholera";
var content_f20_46_21228=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F71548&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F71548&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Oral antibiotics for suspected cholera",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Class",
"       </td>",
"       <td class=\"subtitle1\">",
"        Antibiotic",
"       </td>",
"       <td class=\"subtitle1\">",
"        Typical pediatric dose*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Adult dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comment(s)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        <strong>",
"         Tetracyclines",
"        </strong>",
"       </td>",
"       <td>",
"        Doxycycline",
"       </td>",
"       <td>",
"        4-6 mg/kg (single dose)",
"       </td>",
"       <td>",
"        300 mg (single dose)",
"       </td>",
"       <td rowspan=\"2\">",
"        Antibiotic resistance to all tetracyclines is common",
"        <sup>",
"         [1]",
"        </sup>",
"        . Empiric use is appropriate in epidemics caused by documented susceptible isolates. Not recommended for pregnant women and children less than 8 years.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tetracycline",
"       </td>",
"       <td>",
"        50 mg/kg/day in four equally divided doses, for three days",
"       </td>",
"       <td>",
"        500 mg four times per day for three days",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        <strong>",
"         Macrolides",
"        </strong>",
"       </td>",
"       <td>",
"        Azithromycin",
"       </td>",
"       <td>",
"        20 mg/kg (single dose)",
"       </td>",
"       <td>",
"        1 g (single dose)",
"       </td>",
"       <td rowspan=\"2\">",
"        Single dose azithromycin is preferred therapy",
"        <sup>",
"         [2]",
"        </sup>",
"        . Rare reports of macrolide resistance.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Erythromycin",
"       </td>",
"       <td>",
"        40 mg/kg/day in four equally divided doses, for three days",
"       </td>",
"       <td>",
"        500 mg four times per day for three days",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Fluoroquinolones",
"        </strong>",
"       </td>",
"       <td>",
"        Ciprofloxacin",
"       </td>",
"       <td>",
"        20 mg/kg (single dose)",
"       </td>",
"       <td>",
"        1 g (single dose)",
"       </td>",
"       <td>",
"        Reduced susceptibility to fluoroquinolones has been reported in Asia and Africa",
"        <sup>",
"         [2,3]",
"        </sup>",
"        . Not recommended for pregnant women and children less than 8 years.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Not to exceed maximum dose.",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br>",
"      <ol>",
"       <li>",
"        Yamamoto T, Nair GB, Albert MJ, et al. Survey of in vitro susceptibilities of Vibrio cholerae O1 and O139 to antimicrobial agents. Antimicrob Agents Chemother 1995; 39:241.",
"       </li>",
"       <li>",
"        Saha D, Karim MM, Khan WA, et al. Single-dose azithromycin for the treatment of cholera in adults. N Engl J Med 2006; 354:2452.",
"       </li>",
"       <li>",
"        Islam MS, Midzi SM, Charimari L, et al. Susceptibility to fluoroquinolones of Vibrio cholerae O1 isolated from diarrheal patients in Zimbabwe. JAMA 2009; 302:2321.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
