var title_f20_47_21245="Approach HBV summary";
var content_f20_47_21245=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F63171&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F63171&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for treatment of chronic hepatitis B in adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       HBeAg",
"      </td>",
"      <td class=\"subtitle1\">",
"       HBV DNA (PCR)",
"      </td>",
"      <td class=\"subtitle1\">",
"       ALT",
"      </td>",
"      <td class=\"subtitle1\">",
"       Treatment strategy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"4\">",
"       +",
"      </td>",
"      <td colspan=\"1\" rowspan=\"4\">",
"       &gt;20,000 IU/mL",
"      </td>",
"      <td colspan=\"1\" rowspan=\"4\">",
"       &le;2x ULN",
"      </td>",
"      <td>",
"       Low efficacy with current treatment.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Observe; consider treatment when ALT becomes elevated.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Consider biopsy in persons &gt;40 years, ALT persistently high normal-2x ULN, or with family history of HCC.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Consider treatment if HBV DNA &gt;20,000 IU/mL and biopsy shows moderate/severe inflammation or significant fibrosis.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"11\">",
"       +",
"      </td>",
"      <td colspan=\"1\" rowspan=\"11\">",
"       &gt;20,000 IU/mL",
"      </td>",
"      <td colspan=\"1\" rowspan=\"11\">",
"       &gt;2x ULN",
"      </td>",
"      <td>",
"       Observe for 3-6 months and treat if no spontaneous HBeAg loss.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Consider liver biopsy prior to treatment if compensated.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Immediate treatment if icteric or clinical decompensation.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       IFN&alpha;/peg IFN&alpha;, LAM, ADV, ETV or LdT may be used as initial therapy.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       LAM and LdT not preferred due to high rate of drug resistance.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       End-point of treatment - Seroconversion from HBeAg to anti-HBe.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Duration of therapy:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       IFN&alpha;: 16 weeks",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Peg IFN&alpha;: 48 weeks",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       LAM/ADV/ETV/LdT: minimum 1 year, continue for at least 6 months after HBeAg seroconversion",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       IFN&alpha; non-responders / contraindications to IFN&alpha; &rarr; ADV/ETV.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"6\">",
"       -",
"      </td>",
"      <td colspan=\"1\" rowspan=\"6\">",
"       &gt;20,000 IU/mL",
"      </td>",
"      <td colspan=\"1\" rowspan=\"6\">",
"       &gt;2x ULN",
"      </td>",
"      <td>",
"       IFN&alpha;/peg IFN&alpha;, LAM, ADV, ETV or LdT may be used as initial therapy, LAM and LdT not preferred due to high rate of drug resistance.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       End-point of treatment - not defined.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Duration of therapy:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       IFN&alpha;/peg IFN&alpha;: 1 year",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       LAM/ADV/ETV/LdT: &gt;1 year",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       IFN-&alpha; nonresponders / contraindications to IFN-&alpha; &rarr; ADV/ETV.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       -",
"      </td>",
"      <td>",
"       &gt;2,000 IU/mL",
"      </td>",
"      <td>",
"       1-&gt;2x ULN",
"      </td>",
"      <td>",
"       Consider liver biopsy and treat if liver biopsy shows moderate/severe necroinflammation or significant fibrosis.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       -",
"      </td>",
"      <td>",
"       &le;2,000 IU/mL",
"      </td>",
"      <td>",
"       &le;ULN",
"      </td>",
"      <td>",
"       Observe, treat if HBV DNA or ALT becomes higher.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"5\">",
"       +/-",
"      </td>",
"      <td colspan=\"1\" rowspan=\"5\">",
"       Detectable",
"      </td>",
"      <td colspan=\"1\" rowspan=\"5\">",
"       Cirrhosis",
"      </td>",
"      <td class=\"sublist1_start\">",
"       Compensated:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       HBV DNA &gt;2,000 IU/mL - Treat, LAM/ADV/ETV/LdT may be used as initial therapy. LAM and LdT not preferred due to high rate of drug resistance.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       HBV DNA &lt;2,000 IU/mL - Consider treatment if ALT elevated.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Decompensated:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Coordinate treatment with transplant center, LAM (or LdT) + ADV or ETV preferred. Refer for liver transplant.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"4\">",
"       +/-",
"      </td>",
"      <td colspan=\"1\" rowspan=\"4\">",
"       Undetectable",
"      </td>",
"      <td colspan=\"1\" rowspan=\"4\">",
"       Cirrhosis",
"      </td>",
"      <td class=\"sublist1_start\">",
"       Compensated:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Observe",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Decompensated:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Refer for liver transplant",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ALT: alanine aminotransferase; ULN: upper limit of normal; IFN&alpha;: interferon alpha; peg IFN&alpha;: pegylated IFN alpha; LAM: Lamivudine; ADV: Adefovir; ETV: Entecavir; LdT: Telbivudine.",
"    </div>",
"    <div class=\"reference\">",
"     Lok, AS, McMahon, BJ. AASLD Practice Guideines: Chronic hepatitis B. Hepatology 2007; 45:507.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
