" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of the myeloproliferative neoplasms",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/52/21322/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/52/21322/contributors\">",
"     Ayalew Tefferi, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/52/21322/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
