var title_f20_55_21372="Pneum ILD temsirolimus";
var content_f20_55_21372=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F69423&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F69423&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Suggested guidelines for evaluating and managing respiratory side effects associated with temsirolimus treatment",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Adverse event",
"      </td>",
"      <td class=\"subtitle1\">",
"       Evaluation",
"      </td>",
"      <td class=\"subtitle1\">",
"       Management",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Dyspnea",
"      </td>",
"      <td>",
"       <p>",
"        PFTs",
"       </p>",
"       <p>",
"        Chest X-ray",
"       </p>",
"       <p>",
"        Chest CT scan",
"       </p>",
"       <p>",
"        CBC",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        If imaging does not reveal ILD, provide supportive care and treat other factors contributing to dyspnea (eg, anemia)",
"       </p>",
"       <p>",
"        If imaging suggests interstitial lung disease, see below for management",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       ILD in asymptomatic patients",
"      </td>",
"      <td>",
"       <p>",
"        PFTs",
"       </p>",
"       <p>",
"        Chest CT scan",
"       </p>",
"      </td>",
"      <td>",
"       No specific therapy or drug interruption if PFTs are normal and radiographic changes are mild",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       ILD in patients with mild symptoms",
"      </td>",
"      <td>",
"       <p>",
"        PFTs",
"       </p>",
"       <p>",
"        Chest CT scan",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        May require temporary treatment interruption, depending on severity of PFT and CT abnormalities",
"       </p>",
"       <p>",
"        Will need ongoing monitoring of PFTs and chest CT",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       ILD in patients with underlying lung disease or moderate symptoms/PFT/CT abnormalities",
"      </td>",
"      <td>",
"       <p>",
"        PFTs",
"       </p>",
"       <p>",
"        Chest CT scan",
"       </p>",
"       <p>",
"        Diagnostic testing for opportunistic infection",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        May require temsirolimus discontinuation",
"       </p>",
"       <p>",
"        Will need frequent monitoring of PFTs and chest CT",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       ILD in patients with severe or progressive symptoms/PFT abnormalities",
"      </td>",
"      <td>",
"       <p>",
"        PFTs",
"       </p>",
"       <p>",
"        Chest CT scan",
"       </p>",
"       <p>",
"        Diagnostic testing for opportunistic infection",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Discontinue temsirolimus",
"       </p>",
"       <p>",
"        Administer high dose glucocorticoids",
"       </p>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CBC: complete blood count; CT: computed tomography; DLCO: diffusing capacity for carbon monoxide; ILD: radiographic evidence of interstitial lung disease; PFTs: pulmonary function tests.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Bellmunt, J, Szczylik, C, Feingold, J, et al. Temsirolimus safety profile and management of toxic effects in patients with advanced renal cell carcinoma and poor prognostic features. Ann Oncol 2008; 19:1387.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
