" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Urinary tract infection in renal transplant recipients",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/6/20579/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/6/20579/contributors\">",
"     Mohamed H Sayegh, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/6/20579/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/6/20579/contributors\">",
"     Daniel C Brennan, MD, FACP",
"    </a>",
"    <br/>",
"   </div>",
