var title_f20_6_20587="Efficacy of IFNa in chronic HBV";
var content_f20_6_20587=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F63634&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F63634&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Endpoints for assessing efficacy of IFNa in chronic HBV infection",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Suppression of HBV replication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clearance of HBV DNA from serum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clearance of HBeAg with or without the development anti-HBe antibodies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Improvement in liver disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normalization in ALT level",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decrease in necroinflammatory activity on liver biopsies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Eradication of HBV (which is seldom achieved)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clearance of HBsAg with or without the development of anti-HBs antibodies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clearance of HBV DNA from serum (by PCR); rare in patients who remain HBsAg positive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Disappearance of HBV DNA from liver",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Prevention of cirrhosis and hepatocellular carcinoma and improvement in survival",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Scanty data",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
