" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Prediction of variceal hemorrhage in patients with cirrhosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?20/60/21446/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/60/21446/contributors\">",
"     Arun J Sanyal, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?20/60/21446/contributors\">",
