var title_f20_62_21483="Staging of pressure ulcers";
var content_f20_62_21483=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F62903&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F62903&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Staging of pressure ulcers*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Stage",
"      </td>",
"      <td class=\"subtitle1\">",
"       Description",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       I",
"      </td>",
"      <td>",
"       Skin intact but with non-blanchable redness for &gt;1 hour after relief of pressure.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       II",
"      </td>",
"      <td>",
"       Blister or other break in the dermis with partial thickness loss of dermis, with or without infection.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       III",
"      </td>",
"      <td>",
"       Full thickness tissue loss. Subcutaneous fat may be visible; destruction extends into muscle with or without infection. Undermining and tunneling may be present.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       IV",
"      </td>",
"      <td>",
"       Full thickness skin loss with involvement of bone, tendon, or joint, with or without infection. Often includes undermining and tunneling.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Unstageable",
"      </td>",
"      <td>",
"       Full thickness tissue loss in which the base of the ulcer is covered by slough and/or eschar in the wound bed.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Suspected deep tissue injury",
"      </td>",
"      <td>",
"       Purple or maroon localized area of discolored intact skin or blood-filled blister due to damage of underlying tissue from pressure and/or shear.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * From the National Pressure Ulcer Advisory Panel.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
