var title_f20_62_21484="CKD factors that affect hemostasis";
var content_f20_62_21484=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEPH%2F80195&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEPH%2F80195&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Chronic kidney disease factors that affect hemostasis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Factors predisposing to bleeding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Platelet abnormalities including subnormal dense granule content",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduction in intracellular ADP and serotonin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Impaired release of the platelet &alpha;-granule protein and &beta;-thromboglobulin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Enhanced intracellular cAMP and abnormal mobilization of platelet Ca2+",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abnormal platelet arachidonic acid metabolism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Defective cyclo-oxygenase activity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abnormality of the activation-dependent binding activity of GPIIb/IIIa",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased formation of vascular PGI2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Altered von Willebrand factor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Indirectly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Presence of uremic toxins, especially parathyroid hormone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anemia/altered blood rheology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Erythropoietin deficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Specific drug treatment (eg, nonsteroidal anti-inflammatory drugs)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Procoagulant factors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The high prevalence of systemic inflammation, atherosclerosis and diffuse endothelial damage may provide a substrate for hypercoagulability",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dysfunctional activated protein C metabolism, possibly through multiple mechanisms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The fibrinolytic system is also dysfunctional because of both elevated plasminogen activator inhibitor-1 to tissue-type plasminogen activator ratios and inhibition of plasmin by increased levels of lipoprotein(a)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Defects in the expression of glycoprotein GPIb (the receptor for von Willebrand factor)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ADP: adenosine di-phosphate; cAMP: cyclic adenosine di-phosphate; CKD: chronic kidney disease; GP: glycoprotein; PG: prostaglandin.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Reinecke H, Brand E, Mesters R, et al. Dilemmas in the management of atrial fibrillation in chronic kidney disease. J Am Soc Nephrol 2009; 20:705. Copyright &copy; 2009 American Society of Nephrology.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f20_62_21484=[""].join("\n");
