var title_f20_62_21485="Approach neo thrombocytopenia";
var content_f20_62_21485=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F50212&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F50212&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Approach to the thrombocytopenic newborn infant",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Sick infant",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Neonatal history",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Is there a history of perinatal asphyxia?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Does the child have meconium aspiration syndrome or respiratory distress syndrome?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2_start\">",
"            Is there evidence of infection?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            Necrotizing enterocolitis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            Sepsis/disseminated intravascular coagulation",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2_start\">",
"            Does the child look abnormal?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            Toxoplasmosis, rubella, cytomegalovirus, herpes simplex",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            Chromosome abnormality",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Is the child small for gestational age?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Could the child have a thrombus? Were there indwelling catheters?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Did the child have an intracranial hemorrhage?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Maternal history",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Prenatal or perinatal infection",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Preeclampsia, hemolytic anemia, elevated liver enzymes, low platelet count",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Abnormal placenta",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Neonatal examination",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Head circumference, weight percentile",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Congenital anomalies: radial rays, thumbs, dysmorphic features",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Hepatosplenomegaly",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Blueberry muffin rash",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Hemangioma",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Neonatal laboratory values",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Abnormalities in the white blood cell count or hematocrit",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Hyperbilirubinemia or liver function test abnormalitites",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Evidence of coagulopathy or DIC",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Well infant",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Maternal history",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            History of immune thrombocytopenic purpura or systemic lupus erythematosus?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Previous infant with thrombocytopenia or family history of thrombocytopenic neonate?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Infections during pregnancy?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Drug use during pregnancy?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2_start\">",
"            Maternal platelet count",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            Decreased, suggests autoimmune",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            Normal, may be autoimmune or alloimmune process",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Neonatal examination",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2_start\">",
"            Does the child look abnormal?",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            Thrombocytopenia with absent radii Fanconi anemia",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: George D, Bussel JB. Neonatal thrombocytopenia. Semin Thromb Hemost 1995; 21:276. Copyright &copy; 1995 Theime.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
