var script_f21_1_21521=[" /* <![CDATA[ */",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function Friedewald_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.Friedewald_form){",
"",
"",
"doCalc = true;",
"if (Total_Chol_param.value.indexOf(',') >= 0){ Total_Chol_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Total_Chol_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Total_Chol_unit.options[Total_Chol_unit.selectedIndex].value.split('|');",
"Total_Chol = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (HDL_param.value.indexOf(',') >= 0){ HDL_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(HDL_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = HDL_unit.options[HDL_unit.selectedIndex].value.split('|');",
"HDL = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Triglyceride_param.value.indexOf(',') >= 0){ Triglyceride_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Triglyceride_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Triglyceride_unit.options[Triglyceride_unit.selectedIndex].value.split('|');",
"Triglyceride = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = decpts.options[decpts.selectedIndex].text;",
"LDL =  Total_Chol - (Triglyceride / 5) - HDL;",
"",
"unit_parts = LDL_unit.options[LDL_unit.selectedIndex].value.split('|');",
"if (doCalc) LDL_param.value = fixDP((LDL - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.Friedewald_form){",
"",
"if (Triglyceride_param.value && Triglyceride < 0) {",
"Triglyceride = 0;",
"Triglyceride_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Triglyceride is 0 mg/dL.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Triglyceride_param.value && Triglyceride > 400) {",
"Triglyceride_param.value = \"\";",
"clrResults();",
"Triglyceride = 0;",
"doCalc = false;",
"alert(\"The maximum value for Triglyceride is 400 mg/dL.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.Friedewald_form){",
"",
"LDL_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"/* ]]> */"].join("\n");
