" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Hematologic changes in pregnancy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
