" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Paragangliomas of the head and neck",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?21/13/21720/contributors\">",
