var title_f21_14_21741="Contraindications to fibrinolytic therapy";
var content_f21_14_21741=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F68784%7ECARD%2F62946&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F68784%7ECARD%2F62946&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Absolute and relative contraindications to the use of thrombolytic therapy in patients with acute ST elevation myocardial infarction",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Absolute contraindications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of any intracranial hemorrhage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of ischemic stroke within the preceding three months, with the important exception of acute ischemic stroke seen within three hours which may be treated with thrombolytic therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Presence of a cerebral vascular malformation or a primary or metastatic intracranial malignancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Symptoms or signs suggestive of an aortic dissection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A bleeding diathesis or active bleeding, with the exception of menses; thrombolytic therapy may increase the risk of moderate bleeding, which is offset by the benefits of thrombolysis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Significant closed-head or facial trauma within the preceding three months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Relative contraindications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of chronic, severe, poorly controlled hypertension or uncontrolled hypertension at presentiaton (blood pressure &gt;180 mmHg systolic and/or &gt;110 mmHg diastolic; severe hypertension at presentation can be an absolute contraindication in patients at low risk)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of ischemic stroke more than three months previously",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dementia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Any known intracranial disease that is not an absolute contraindication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Traumatic or prolonged (&gt;10 min) cardiopulmonary resuscitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Major surgery within the preceding three weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Internal bleeding within the preceding two to four weeks or an active peptic ulcer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Noncompressible vascular punctures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pregnancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Current warfarin therapy - the risk of bleeding increases as the INR increases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        For streptokinase or anistreplase - a prior exposure (more than five days previously) or allergic reaction to these drugs",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * May not be all-inclusive or definitive.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Antman EM, Anbe DT, Armstrong PW, et al. ACC/AHA guidelines for the management of patients with ST-elevation myocardial infarction--executive summary: a report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing Committee to Revise the 1999 Guidelines for the Management of Patients With Acute Myocardial Infarction). Circulation 2004; 110:588.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cooperative cardiovascular project risk model for intracranial hemorrhage with thrombolytic therapy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\" rowspan=\"1\">",
"        Risk",
"Factors*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        Age &ge;75 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        Black race",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        Female sex",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        Prior history of stroke",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        Systolic blood pressure",
"      &ge;160 mmHg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        Weight &le;65 kg for women or &le;80 kg for men",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        INR &gt;4 or PT",
"&gt;24&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\" rowspan=\"1\">",
"        Use of alteplase",
"(versus other thrombolytic agent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Risk score",
"       </td>",
"       <td class=\"subtitle1_single\">",
"        Rate of intracranial",
"hemorrhage, percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        0 or 1",
"       </td>",
"       <td>",
"        0.69",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2",
"       </td>",
"       <td>",
"        1.02",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3",
"       </td>",
"       <td>",
"        1.63",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4",
"       </td>",
"       <td>",
"        2.49",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &ge;5",
"       </td>",
"       <td>",
"        4.11",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Each risk factor is worth 1 point if present, 0 points if absent. Points are added to determine the risk score.",
"     <br>",
"      &bull; INR: international normalized ratio; PT: prothrombin time.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from Brass LM, Lichtman JH, Wang Y, et al. Intracranial hemorrhage associated with thrombolytic therapy for elderly patients with acute myocardial infarction: results from the cooperative cardiovascular project. Stroke 2000; 31:1802.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
