" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   The global human immunodeficiency virus pandemic",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?21/2/21546/contributors\">",
