"     </li>",
"     <li>",
"      Fiberall&reg; [OTC];",
"     </li>",
"     <li>",
"      Fibro-Lax [OTC];",
"     </li>",
"     <li>",
"      Fibro-XL [OTC];",
"     </li>",
"     <li>",
"      Hydrocil&reg; Instant [OTC];",
"     </li>",
"     <li>",
"      Konsyl-D&trade; [OTC];",
"     </li>",
"     <li>",
"      Konsyl&reg; Easy Mix&trade; [OTC];",
"     </li>",
"     <li>",
"      Konsyl&reg; Orange [OTC];",
"     </li>",
"     <li>",
"      Konsyl&reg; Original [OTC];",
"     </li>",
"     <li>",
"      Konsyl&reg; [OTC];",
"     </li>",
"     <li>",
