var title_f21_24_21899="Preventing dietary lead exposure";
var content_f21_24_21899=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F54388&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F54388&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Nutritional interventions to prevent lead exposure and absorption",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Provide a balanced diet that provides a combination of fruits, vegetables, grains, protein, and dairy products",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ensure children receive sufficient amounts of calcium, iron, zinc, and vitamin C",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reduce consumption of foods high in fat",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do not store food or liquids in lead crystal glassware or imported or old pottery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If you reuse plastic bags to store or carry food, keep the printing on the outside of the bag",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Avoid eating produce that is grown in soil with a high lead content or sprayed with insecticides that contain lead",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        If lead-contaminated water is a concern:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Flush pipes for one minute before drinking water or using it for cooking",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Use only cold water for cooking, drinking, or preparing infant formula",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Consider buying a HEPA* filter certified for lead removal. Call the Environmental Protection Agency's Safe Drinking Water Hotline: 800-426-4791.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * High-efficiency particulate air.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: www.epa.gov/lead/educationexesum.htm and www.psr.org/childled.htm.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
