" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Initial management of regionally localized intermediate and high risk prostate cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?21/26/21930/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?21/26/21930/contributors\">",
"     John F Ward, MD, FACS",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?21/26/21930/contributors\">",
"     Nicholas Vogelzang, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?21/26/21930/contributors\">",
"     Brian Davis, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?21/26/21930/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?21/26/21930/contributors\">",
"     W Robert Lee, MD, MS, MEd",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?21/26/21930/contributors\">",
"     Jerome P Richie, MD, FACS",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?21/26/21930/contributors\">",
"     Deputy Editor",
