" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   MDMA (ecstasy) intoxication",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?21/3/21560/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?21/3/21560/contributors\">",
"     Robert J Hoffman, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?21/3/21560/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?21/3/21560/contributors\">",
"     Stephen J Traub, MD",
"    </a>",
"    <br/>",
"   </div>",
