var title_f21_31_22014="Processed EEG brain monitors";
var content_f21_31_22014=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=SURG%2F81084&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=SURG%2F81084&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Processed EEG brain monitors",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Monitor*",
"      </td>",
"      <td class=\"subtitle1\">",
"       Manufacturer&bull;",
"      </td>",
"      <td class=\"subtitle1\">",
"       Parameters",
"      </td>",
"      <td class=\"subtitle1\">",
"       Monitoring range",
"      </td>",
"      <td class=\"subtitle1\">",
"       Notes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"6\">",
"       Unprocessed EEG",
"      </td>",
"      <td rowspan=\"6\">",
"       Non-proprietary",
"      </td>",
"      <td rowspan=\"6\">",
"       Alpha, beta, gamma, delta, theta waves, sleep spindles; K complexes, spectral edge frequency, burst suppression; Isoelectricity",
"      </td>",
"      <td>",
"       No specific index. Permutation entropy can be derived.",
"      </td>",
"      <td>",
"       <p>",
"        Advantages: rapid response time, cheap, non proprietary.",
"       </p>",
"       <p>",
"        Disadvantages: No index or value, requires attention, requires training.",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       High frequency, low amplitude (gamma and beta dominance)",
"      </td>",
"      <td>",
"       Wakefulness common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Alpha oscillations",
"      </td>",
"      <td>",
"       Drowsiness/sedation common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Spindles, K complexes, some delta waves",
"      </td>",
"      <td>",
"       Explicit recall rare",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Slow delta waves, spectral edge &lt;12",
"      </td>",
"      <td>",
"       Surgical anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Burst suppression and isoelectricity",
"      </td>",
"      <td>",
"       Deep anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"6\">",
"       Bispectral index (BIS)",
"      </td>",
"      <td rowspan=\"6\">",
"       Covidien Medical",
"      </td>",
"      <td rowspan=\"6\">",
"       Bispectral index (proprietary algorithm) based upon beta power, bispectral coherence (synchfastslow), and burst suppression ratio",
"      </td>",
"      <td>",
"       0 to 100",
"      </td>",
"      <td>",
"       <p>",
"        Raw EEG and parameters such as spectral edge frequency can and should be displayed.",
"       </p>",
"       <p>",
"        Advantages: Index value can be used to guide anesthesia. May improve outcomes.",
"       </p>",
"       <p>",
"        Disadvantages: Slow response time, costly, proprietary.",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       80 to 100",
"      </td>",
"      <td>",
"       Wakefulness common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       60 to 80",
"      </td>",
"      <td>",
"       Drowsiness/sedation common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       50 to 60",
"      </td>",
"      <td>",
"       Explicit recall rare",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       30 to 50",
"      </td>",
"      <td>",
"       Surgical anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       0 to 30",
"      </td>",
"      <td>",
"       Deep anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"6\">",
"       Entropy",
"      </td>",
"      <td rowspan=\"6\">",
"       GE Healthcare",
"      </td>",
"      <td rowspan=\"6\">",
"       State entropy (SE) and response entropy (RE) indices; burst suppression ratio; Entropy of EEG signal decreases with sleep and anesthesia",
"      </td>",
"      <td>",
"       <p>",
"        SE: (0 to 100)",
"       </p>",
"       <p>",
"        RE: (0 to 91)",
"       </p>",
"      </td>",
"      <td>",
"       State entropy (SE)of EEG signal calculated up to 32 Hz. Response entropy (RE) includes additional frequencies up to 47 Hz. SE is always &le;RE. Raw EEG can and should be displayed.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       80 to 100",
"      </td>",
"      <td>",
"       Wakefulness common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Sudden drop in entropy",
"      </td>",
"      <td>",
"       May be associated with loss of responsiveness",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       40 to 60",
"      </td>",
"      <td>",
"       <p>",
"        Explicit recall rare",
"       </p>",
"       <p>",
"        Surgical anesthesia common",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Sudden rise in RE",
"      </td>",
"      <td>",
"       May indicate pain or muscle activity",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lower entropy numbers and burst suppression",
"      </td>",
"      <td>",
"       Deep anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"6\">",
"       <p>",
"        A-line&reg;",
"       </p>",
"       <p>",
"        AEP Monitor/2&reg;",
"       </p>",
"       <p>",
"        (Adds EEG parameters)",
"       </p>",
"      </td>",
"      <td rowspan=\"6\">",
"       Danmeter a/s",
"      </td>",
"      <td rowspan=\"6\">",
"       Middle latency auditory evoked potentials (MLAEPs); ARX Auditory Index (AAI); Middle latency (20 to 80 msec); Composite AAI Index (Proprietary - combines EEG and MLAEP information)",
"      </td>",
"      <td>",
"       0 to 100",
"      </td>",
"      <td>",
"       Full range",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       60 to 100",
"      </td>",
"      <td>",
"       Wakefulness common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       40 to 60",
"      </td>",
"      <td>",
"       Drowsiness/sedation common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       25 to 40",
"      </td>",
"      <td>",
"       Light anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       15 to 25",
"      </td>",
"      <td>",
"       Surgical anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       0 to 15",
"      </td>",
"      <td>",
"       Deep anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"7\">",
"       <p>",
"        Cerebral State&reg;",
"       </p>",
"       <p>",
"        Hand-held device",
"       </p>",
"       <p>",
"        Wireless connection to anesthesia monitor",
"       </p>",
"      </td>",
"      <td rowspan=\"7\">",
"       Danmeter a/s",
"      </td>",
"      <td rowspan=\"7\">",
"       Cerebral State Index (CSI) (Proprietary); Burst suppression electromyography and signal quality are indicated; Index derived mainly from alpha and beta EEG ratios, burst suppression",
"      </td>",
"      <td>",
"       0 to 100",
"      </td>",
"      <td>",
"       Full range",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       90 to 100",
"      </td>",
"      <td>",
"       Wakefulness common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       80 to 90",
"      </td>",
"      <td>",
"       Drowsiness common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       60 to 80",
"      </td>",
"      <td>",
"       Light anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       40 to 60",
"      </td>",
"      <td>",
"       Surgical anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       10 to 40",
"      </td>",
"      <td>",
"       Deep anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       0 to 10",
"      </td>",
"      <td>",
"       Burst suppression or isoelectric EEG",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"7\">",
"       Narcotrend&reg;",
"      </td>",
"      <td rowspan=\"7\">",
"       MT MonitorTechnik GmbH &amp; Co. KG",
"      </td>",
"      <td rowspan=\"7\">",
"       Cerebrogram (Proprietary); Raw EEG waveform displayed; Median and spectral edge frequencies; Power in EEG frequency bands is shown",
"      </td>",
"      <td>",
"       A to F",
"      </td>",
"      <td>",
"       Full range",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       A",
"      </td>",
"      <td>",
"       Wakefulness common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       B",
"      </td>",
"      <td>",
"       Drowsiness common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       C",
"      </td>",
"      <td>",
"       Light anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       D",
"      </td>",
"      <td>",
"       Surgical anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       E",
"      </td>",
"      <td>",
"       Deep anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       F",
"      </td>",
"      <td>",
"       Burst suppression or isoelectric EEG",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"6\">",
"       SEDline&reg;",
"      </td>",
"      <td rowspan=\"6\">",
"       Hospira",
"      </td>",
"      <td rowspan=\"6\">",
"       Patient state index (PSI) (Proprietary); Raw EEG waveform displayed; Density spectral array (DSA) shown; Converts EEG acquired from four active, ground and reference electrodes into a proprietary index; Anteriorization of EEG power; loss of coherence",
"      </td>",
"      <td>",
"       0 to 100",
"      </td>",
"      <td>",
"       Full range",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       90 to 100",
"      </td>",
"      <td>",
"       Wakefulness common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       50 to 90",
"      </td>",
"      <td>",
"       Drowsiness/sedation common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       25 to 50",
"      </td>",
"      <td>",
"       Surgical anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       10 to 25",
"      </td>",
"      <td>",
"       Deep anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       0 to 10",
"      </td>",
"      <td>",
"       Burst suppression or isoelectric EEG",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"5\">",
"       SNAP II&reg;",
"      </td>",
"      <td rowspan=\"5\">",
"       Stryker",
"      </td>",
"      <td rowspan=\"5\">",
"       SNAP index (Proprietary); Spectral analysis of low (0-18 Hz) and high (&gt;80 Hz) frequency EEG components; PDA portable device; Raw EEG waveform displayed",
"      </td>",
"      <td>",
"       0 to 100",
"      </td>",
"      <td>",
"       Full range",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       90 to 100",
"      </td>",
"      <td>",
"       Wakefulness common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       65 to 90",
"      </td>",
"      <td>",
"       Drowsiness/sedation common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       50 to 65",
"      </td>",
"      <td>",
"       Surgical anesthesia common",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       0 to 50",
"      </td>",
"      <td>",
"       Deep anesthesia common",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * All brain monitors are not specific for the anesthetic state. Patient morbidity, drugs and even natural sleep can confound anesthetic depth monitoring.",
"     <br>",
"      &bull; Specific manufacturer guidelines for use may vary.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
