var title_f21_33_22044="Failure to respond to iron";
var content_f21_33_22044=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F56310&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F56310&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes for failure to respond to oral iron therapy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Coexisting disease interfering with marrow response",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inflammatory disorder (eg, rheumatoid arthritis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Concomitant malignancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Coexisting folic acid and/or vitamin B12 deficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bone marrow suppression from another cause",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Diagnosis is incorrect, possible correct diagnoses include",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thalassemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lead poisoning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anemia of chronic disease (anemia of chronic inflammation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Copper deficiency (zinc toxicity)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Myelodysplastic syndrome/refractory sideroblastic anemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Patient is not taking the medication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prescription has not been filled",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prescription has been filled but patient is no longer taking the medication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Medication is being taken but is not being absorbed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rapid intestinal transport bypasses area of maximum absorption",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Enteric coated product: coating is not dissolving",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patient has malabsorption for iron (eg, sprue, atrophic gastritis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Medication taken in association with an agent interfering with absorption (eg, antacids, tetracycline, tea)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Continued blood loss or need in excess of iron dose ingested",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Cause of blood loss treatable (eg, bleeding peptic ulcer)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Initiate appropriate treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Cause of blood loss not treatable (eg, Osler Weber Rendu disease) or need cannot be met by oral iron preparation (eg, renal failure responding to erythropoietin)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Switch patient to parenteral iron product",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     Assumes that original diagnosis was iron deficiency anemia with hypochromic microcytic red blood cells, low ferritin, and low tranferrin saturation.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
