var title_f21_33_22045="MTX ectopic preg protocol";
var content_f21_33_22045=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F56738&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F56738&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Methotrexate treatment protocol for tubal or interstitial ectopic pregnancy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"3\">",
"       Pretreatment testing and instructions",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       hCG concentration",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       Transvaginal ultrasound",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       Blood group and Rh(D) typing; give anti Rh(D) immune globulin 300 mcg IM, if indicated",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       Complete blood count",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       Liver and renal function tests",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       Discontinue folic acid supplements",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       Counsel patient to avoid nonsteroidal antiinflammatory medications, recommend acetaminophen if an analgesic is needed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       Advise patient to refrain from sexual intercourse and strenuous exercise",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Treatment day",
"      </td>",
"      <td class=\"subtitle1\">",
"       Single dose protocol",
"      </td>",
"      <td class=\"subtitle1\">",
"       Multiple dose protocol",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"2\">",
"       1",
"      </td>",
"      <td>",
"       hCG concentration",
"      </td>",
"      <td>",
"       hCG concentration",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       MTX 50 mg/m2 body surface area IM",
"      </td>",
"      <td>",
"       MTX 1mg/kg bodyweight IM",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       2",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       LEU 0.1 mg/kg PO",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"3\">",
"       3",
"      </td>",
"      <td rowspan=\"3\">",
"       &nbsp;",
"      </td>",
"      <td>",
"       hCG",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If &lt;15 percent hCG decline from day 1 to 3, give MTX 1 mg/kg IM",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If &ge;15 percent decline from day 1 to 3, begin weekly hCG",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       4",
"      </td>",
"      <td>",
"       hCG (protocols vary, see day 7)",
"      </td>",
"      <td>",
"       LEU 0.1 mg/kg PO*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"3\">",
"       5",
"      </td>",
"      <td rowspan=\"3\">",
"       &nbsp;",
"      </td>",
"      <td>",
"       hCG",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If &lt;15 percent decline from day 3 to 5 MTX, give MTX 1 mg/kg IM",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If &ge;15 percent decline from day 3 to 5, begin weekly hCG",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       6",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       LEU 0.1 mg/kg PO*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"3\">",
"       7",
"      </td>",
"      <td>",
"       hCG",
"      </td>",
"      <td>",
"       hCG",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If &lt;15 percent hCG decline from day 4 to 7 (OR &lt;25 percent decline from day 1 to 7), give additional dose of MTX 50 mg/m2 IM",
"      </td>",
"      <td>",
"       If &lt;15 percent decline from day 5 to 7 MTX, give MTX 1 mg/kg IM",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If &ge;15 percent hCG decline from day 4 to 7 (OR &ge;25 percent decline from day 1 to 7), draw hCG concentration weekly until hCG is undetectable",
"      </td>",
"      <td>",
"       If &ge;15 percent decline from day 5 to 7, begin weekly hCG",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       8",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       LEU 0.1 mg/kg PO*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"3\">",
"       14",
"      </td>",
"      <td>",
"       hCG",
"      </td>",
"      <td>",
"       hCG",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If &lt;15 percent hCG decline from day 7 to 14, give additional dose of MTX 50 mg/m2 IM",
"      </td>",
"      <td>",
"       If &lt;15 percent hCG decline from day 7 to 14, give additional dose of MTX 1 mg/kg IM (give LEU 0.1 mg/kg PO on day 15)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If &ge;15 percent hCG decline from day 7 to 14, check hCG weekly until undetectable",
"      </td>",
"      <td>",
"       If &ge;15 percent hCG decline from day 7 to 14, check hCG weekly until undetectable",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       21 and 28",
"      </td>",
"      <td>",
"       If 3 doses have been given and there is a &lt;15 percent hCG decline from day 21 to 28, proceed with laparocopic surgery",
"      </td>",
"      <td>",
"       If 5 doses have been given and there is a &lt;15 percent hCG decline from day 14 to 21, proceed with laparoscopic surgery",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"3\">",
"       Laparoscopy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       If severe abdominal pain or an acute abdomen suggestive of tubal rupture occurs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       If ultrasonography reveals greater than 300 mL pelvic or other intraperitoneal fluid",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     hCG: human chorionic gonadotropin beta-subunit; MTX: methotrexate; IM: intramuscular; LEU: leucovorin; PO: oral route.",
"     <br>",
"      * Leucovorin is given only if methotrexate was given on the previous day.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
