var title_f21_34_22059="Diarrhea referral indications";
var content_f21_34_22059=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F66399&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F66399&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Indications for referral for medical evaluation in children with acute diarrhea",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Young age (eg, age &lt;6 months or weight &lt;8",
"kg)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of premature birth, chronic medical conditions,",
"or concurrent illness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fever (&ge;38&deg;C",
"for infants &lt;3 months or &ge;39&deg;C",
"for children 3 to 36 months)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Visible blood in stool",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Frequent and substantial volumes of diarrhea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persistent vomiting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Caregiver's report of signs consistent with dehydration",
"(eg, sunken eyes or decreased tears, dry mucous membranes, or decreased",
"urine output)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Change in mental status (eg, irritability, apathy, or",
"lethargy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Suboptimal response to oral rehydration therapy already",
"administered",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inability of the caregiver to administer oral",
"rehydration therapy",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from King, CK, Glass, R, Bresee, JS, Duggan, C. Managing acute gastroenteritis among children: oral rehydration, maintenance, and nutritional therapy. MMWR Recomm Rep 2003; 52:1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
