" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of mild to moderate ulcerative colitis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?21/36/22090/contributors\">",
