var title_f21_37_22109="Thyrotoxicosis child";
var content_f21_37_22109=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F58890&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F58890&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of thyrotoxicosis in children and adolescents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"6\" width=\"16%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Name",
"       </td>",
"       <td class=\"subtitle1\">",
"        Synonyms",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism",
"       </td>",
"       <td class=\"subtitle1\">",
"        Antibody tests",
"       </td>",
"       <td class=\"subtitle1\">",
"        RAI",
"       </td>",
"       <td class=\"subtitle1\">",
"        Thyroid exam",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"6\">",
"        Thyroid gland hyperfunction (increased synthesis of thyroid hormones)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Graves' disease",
"       </td>",
"       <td>",
"        <p>",
"         Hyperthyroid goiter",
"        </p>",
"        von Basedow's disease",
"       </td>",
"       <td>",
"        Thyrotropin receptor-stimulating antibodies (TRS-Ab)",
"       </td>",
"       <td>",
"        TRS-Ab positive; TPO-Ab positive or negative; TBII positive",
"       </td>",
"       <td>",
"        High, diffuse",
"       </td>",
"       <td>",
"        Symmetric nontender goiter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Multinodular hyperthyroid goiter",
"       </td>",
"       <td>",
"        Toxic multinodular goiter",
"       </td>",
"       <td>",
"        Autonomous overproduction of thyroid hormone by nodules",
"       </td>",
"       <td>",
"        All negative",
"       </td>",
"       <td>",
"        Normal or high, multifocal",
"       </td>",
"       <td>",
"        Nodular",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Autonomous nodule",
"       </td>",
"       <td>",
"        <p>",
"         Plummer's disease",
"        </p>",
"        Toxic nodule",
"       </td>",
"       <td>",
"        Same",
"       </td>",
"       <td>",
"        All negative",
"       </td>",
"       <td>",
"        High, single focus; rest of gland suppressed",
"       </td>",
"       <td>",
"        Single nodule",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        TSH-producing pituitary adenoma",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Autonomous overproduction of TSH",
"       </td>",
"       <td>",
"        All negative",
"       </td>",
"       <td>",
"        High",
"       </td>",
"       <td>",
"        Normal or symmetric goiter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pituitary resistance to thyroid hormone",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Overproduction of TSH",
"       </td>",
"       <td>",
"        All negative",
"       </td>",
"       <td>",
"        High, diffuse",
"       </td>",
"       <td>",
"        Symmetric goiter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"6\">",
"        Thyrotoxic phase of thyroiditis (excess secretion of preformed thyroid hormones)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Thyrotoxic phase of chronic lymphocytic (Hashimoto's) thyroiditis",
"       </td>",
"       <td>",
"        <p>",
"         Hashitoxicosis",
"        </p>",
"        Lymphadenoid goiter",
"       </td>",
"       <td>",
"        Autoimmune; release of preformed hormone. Predominantly women.",
"       </td>",
"       <td>",
"        TPO-Ab positive; thyroglobulin positive; TRS-Ab positive or negative",
"       </td>",
"       <td>",
"        Low in majority, but can be elevated if TRS-Ab positive; inhomogeneous pattern",
"       </td>",
"       <td>",
"        Firm goiter, sometimes tender",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Subacute lymphocytic thyroiditis",
"       </td>",
"       <td>",
"        <p>",
"         Painless sporadic thyroiditis",
"        </p>",
"        Silent thyroiditis",
"       </td>",
"       <td>",
"        Autoimmune; release of preformed hormone. May also be associated with drugs (IFN-alpha, IL-2, lithium).",
"       </td>",
"       <td>",
"        TPO-Ab positive",
"       </td>",
"       <td>",
"        Low (during thyrotoxic phase)",
"       </td>",
"       <td>",
"        Firm nontender, mildly enlarged thyroid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Subacute granulomatous thyroiditis",
"       </td>",
"       <td>",
"        <p>",
"         Subacute thyroiditis",
"        </p>",
"        <p>",
"         Painful subacute thyroiditis",
"        </p>",
"        <p>",
"         de Quervain's thyroiditis",
"        </p>",
"        Granulomatous giant cell thyroiditis",
"       </td>",
"       <td>",
"        Viral; release of preformed hormone",
"       </td>",
"       <td>",
"        TPO-Ab negative; elevated ESR",
"       </td>",
"       <td>",
"        Low (during thyrotoxic phase)",
"       </td>",
"       <td>",
"        Painful goiter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"6\">",
"        Drug-induced thyrotoxicosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Factitous thyroiditis",
"       </td>",
"       <td>",
"        Thyrotoxicosis factitia",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Low TSH",
"       </td>",
"       <td>",
"        Low or absent",
"       </td>",
"       <td>",
"        No goiter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Iodine-induced hyperthyroidism",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Underlying multinodular goiter; thyroid hormone release triggered by exposure to iodine (eg, contrast agents, amiodarone)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Increased",
"       </td>",
"       <td>",
"        Often multinodular",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     TRS-Ab: thyrotropin-receptor stimulating antibodies; TPO-Ab: thyroid peroxidase antibodies; TBII: thyrotropin binding inhibitor immunoglobulin.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
