" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 470px\">",
"   <div class=\"ttl\">",
"    Mitral annular calcification",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 258px; height: 251px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
