var title_f21_44_22221="Classes of ERCP complications";
var content_f21_44_22221=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F73512%7EGAST%2F71241&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F73512%7EGAST%2F71241&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of site, timing, and severity of complications related to endoscopic retrograde cholangiopancreatography (ERCP)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Site",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Focal",
"       </td>",
"       <td>",
"        Occurring at the point of endoscopic contact",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nonspecific",
"       </td>",
"       <td>",
"        Occurring in organs not traversed or treated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Timing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Immediate",
"       </td>",
"       <td>",
"        Occurring during ERCP",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Early",
"       </td>",
"       <td>",
"        Evident within the recovery period",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Delayed",
"       </td>",
"       <td>",
"        Focal (occurring within 30 days)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nonspecific (first symptom presenting within 3 days)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Late",
"       </td>",
"       <td>",
"        Evident after months or years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Criteria for severity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Length of stay",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mild",
"       </td>",
"       <td>",
"        &le;3 nights",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Moderate",
"       </td>",
"       <td>",
"        4 to 10 nights",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Severe",
"       </td>",
"       <td>",
"        &gt;10 nights, ICU admission, or surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fatal",
"       </td>",
"       <td>",
"        Death attributable to the procedure within 30 days (or longer if under continued in-patient treatment for the complication)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Other indices of severity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Need for blood transfusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Additional interventions (endoscopic or radiologic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Total length of stay",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        Permanent residual disability",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Cotton PB, Gastrointest Endosc 1994; 40:514 and Aliperti G, et al, Gastrointest Endosc Clin N Am 1996; 6:379.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Grading system for the major complications of endoscopic retrograde cholangiopancreatography (ERCP) and endoscopic sphincterotomy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"10%\">",
"     </colgroup>",
"     <colgroup span=\"3\" width=\"30%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mild",
"       </td>",
"       <td class=\"subtitle1\">",
"        Moderate",
"       </td>",
"       <td class=\"subtitle1\">",
"        Severe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Pancreatitis",
"        </strong>",
"       </td>",
"       <td>",
"        Amylase at least three times normal at more than 24 hours after the procedure, requiring admission or prolongation of planned admission to two to three days",
"       </td>",
"       <td>",
"        Hospitalization of 4 to 10 days",
"       </td>",
"       <td>",
"        Hospitalization of more than ten days, hemorrhagic pancreatitis, phlegmon or pseudocyst, or intervention (percutaneous drainage or surgery)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Bleeding",
"        </strong>",
"       </td>",
"       <td>",
"        Clinical (not just endoscopic) evidence of bleeding, hemoglobin drop &lt;3 g, and no need for transfusion",
"       </td>",
"       <td>",
"        Transfusion (four units or less), no angiographic intervention or surgery",
"       </td>",
"       <td>",
"        Transfusion (five units or more) or intervention (angiographic or surgical)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Cholangitis",
"        </strong>",
"       </td>",
"       <td>",
"        Temperature of &gt;38&deg;C for 24 to 48 hours",
"       </td>",
"       <td>",
"        Febrile or septic illness requiring more than three days of hospital treatment or endoscopic or percutaneous intervention",
"       </td>",
"       <td>",
"        Septic shock or surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Perforation",
"        </strong>",
"       </td>",
"       <td>",
"        Possible, or only very slight leak of fluid or contrast, treatable by fluids and suction for three days or less",
"       </td>",
"       <td>",
"        Any definite perforation treated medically for 4 to 10 days",
"       </td>",
"       <td>",
"        Medical treatment for more than 10 days, or intervention (percutaneous or surgical)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from: Cotton PB, et al. Endoscopic sphincterotomy complications and their management: An attempt at consensus. Gastrointest Endosc 1991; 37:383.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
