" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of seasonal influenza in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
