var script_f21_47_22257=[" /* <![CDATA[ */",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function InFlightPaO2_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 3000);",
"",
"with(document.InFlightPaO2_form){",
"",
"",
"doCalc = true;",
"if (Altitude_param.value.indexOf(',') >= 0){ Altitude_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Altitude_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Altitude_unit.options[Altitude_unit.selectedIndex].value.split('|');",
"Altitude = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Sea_Level_PaO2_param.value.indexOf(',') >= 0){ Sea_Level_PaO2_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Sea_Level_PaO2_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Sea_Level_PaO2_unit.options[Sea_Level_PaO2_unit.selectedIndex].value.split('|');",
"Sea_Level_PaO2 = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"if (Sea_Level_PaCO2_param.value.indexOf(',') >= 0){ Sea_Level_PaCO2_param.value = ''; doCalc = false; alert('Comma character not allowed'); }",
"param_value = parseFloat(Sea_Level_PaCO2_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Sea_Level_PaCO2_unit.options[Sea_Level_PaCO2_unit.selectedIndex].value.split('|');",
"Sea_Level_PaCO2 = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = decpts.options[decpts.selectedIndex].text;",
"PaO2 =  1.59 + (0.98 * Sea_Level_PaO2) + (0.0031 * Altitude) - (0.000061 * Sea_Level_PaO2 * Altitude) - (0.000065 * Sea_Level_PaCO2 * Altitude) + (0.000000092 * power(Altitude,2));",
"",
"unit_parts = PaO2_unit.options[PaO2_unit.selectedIndex].value.split('|');",
"if (doCalc) PaO2_param.value = fixDP((PaO2 - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.InFlightPaO2_form){",
"",
"if (Altitude_param.value && Altitude < 0) {",
"Altitude = 0;",
"Altitude_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Altitude is 0 feet.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Altitude_param.value && Altitude > 8000) {",
"Altitude_param.value = \"\";",
"clrResults();",
"Altitude = 0;",
"doCalc = false;",
"alert(\"The maximum value for Altitude is 8000 feet.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.InFlightPaO2_form){",
"",
"PaO2_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"/* ]]> */"].join("\n");
