"    <p style=\"text-indent:-2em;margin-left:2em;text-align:justify;\">",
"     <b>",
"      Tablet, 24-hour",
"     </b>",
"     (Sular Oral)",
"    </p>",
"    <p style=\"text-indent:-2em;margin-left:4em;text-align:justify;\">",
"     8.5 mg (100): $1417.50",
"    </p>",
"    <p style=\"text-indent:-2em;margin-left:4em;text-align:justify;\">",
"     17 mg (100): $1417.50",
"    </p>",
"    <p style=\"text-indent:-2em;margin-left:4em;text-align:justify;\">",
"     34 mg (100): $1417.50",
"    </p>",
"   </div>",
"   <div class=\"block mop drugH1Div\" id=\"F6063543\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     Monitoring Parameters",
"    </span>",
"    <p style=\"text-indent:0em;display:inline\">",
"     Blood pressure, heart rate",
"    </p>",
"   </div>",
"   <div class=\"list fbnlist drugH1Div drugBrandNames\" id=\"F201803\" xmlns=\"file://www.w3.org/1999/xhtml\">",
"    <span class=\"drugH1\">",
"     International Brand Names",
"    </span>",
"    <ul>",
"     <li>",
"      Angiolat (UY);",
"     </li>",
"     <li>",
"      Baymycard (BG, DE, HN, HU, JP);",
