var title_f21_49_22299="Considerations for the diagnosis of pediatric RLS";
var content_f21_49_22299=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F86838&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F86838&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Important considerations for the diagnosis of pediatric restless legs syndrome",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <ul>",
"         <li>",
"          The child must describe the RLS symptoms in his/her own words",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <ul>",
"         <li>",
"          The diagnostician should be aware of the typical words children and adolescents use to describe RLS",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <ul>",
"         <li>",
"          Language and cognitive development determine the applicability of the RLS diagnostic criteria, rather than age",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <ul>",
"         <li>",
"          As in adults, a significant impact on sleep, mood, cognition, and function is found. However, impairment is manifest more often in behavioral and educational domains.",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <ul>",
"         <li>",
"          Periodic limb movement disorder (PLMD) may precede the diagnosis of RLS in some cases",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     RLS: restless legs syndrome; PLMD: periodic limb movement disorder.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: the International Restless Legs Syndrome Study Group (",
"     <a href=\"file://www.irlssg.org\" target=\"_blank\">",
"      www.irlssg.org",
"     </a>",
"     ). Copyright &copy; 2012.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
