" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Acute colonic pseudo-obstruction (Ogilvie's syndrome)",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?21/50/22311/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?21/50/22311/contributors\">",
"     Michael Camilleri, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?21/50/22311/contributors\">",
"     Section Editor",
