var title_f21_51_22331="Performance of PEF maneuver";
var content_f21_51_22331=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F53856&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F53856&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Performance of peak flow maneuver",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Explain and demonstrate the test*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Zero the PEF monitor, if necessary",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stand up straight",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhale completely; the inhalation should be rapid but",
"not forced",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Place PEF monitor in mouth and close lips around the",
"mouthpiece&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Exhale with maximal force&Delta;",
"as soon as lips are",
"sealed around mouthpiece&loz;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Write down results",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Repeat two or more times (three total)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Record all three values",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Not necessary if at home.",
"     <br>",
"      &bull; Nose clips are not necessary.",
"      <br>",
"       &Delta; Make sure subject understands to make full use of respiratory muscles, not just use the diaphram as a \"toot\" or \"mouth\" maneuver.",
"       <br>",
"        &loz; D'Angelo and coworkers [65] have reported that PEF is reduced when, during the inspiratory maneuver, there is a four to six second pause at TLC before beginning exhalation. It is not known if similar changes will be observed with portable peak flow meters.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
