"    <li class=\"plainItem\">",
"     <a class=\"drug drug_pediatric\" href=\"UTD.htm?27/58/28582?source=related_link\">",
"      Basiliximab: Pediatric drug information",
"     </a>",
"    </li>",
"   </ul>",
"  </div>",
