" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Microbiology and pathogenesis of tuberculosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?21/54/22378/contributors\">",
"     Author",
"    </a>",
