var title_f21_55_22396="Low-fat low-sugar guide A";
var content_f21_55_22396=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F67313&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F67313&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Tips for maintaining a healthy weight: Low-fat, low-sugar eating guidelines",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"20%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Food group",
"       </td>",
"       <td class=\"subtitle1\">",
"        Food to choose more often",
"       </td>",
"       <td class=\"subtitle1\">",
"        Food to limit",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Breads, cereals, starches and starchy vegetables",
"       </td>",
"       <td>",
"        Barley, bulgur, buckwheat (kasha); whole grain breads, bagels, English muffins, hamburger buns, rolls, tortillas, crackers, cereals, pancakes, and waffles; brown rice, whole wheat pasta, whole wheat couscous, unflavored oatmeal, winter squash, sweet potatoes, corn, corn tortillas",
"       </td>",
"       <td>",
"        Biscuits; store brought muffins, croissants, sweet rolls, Danish, and donuts; sugar coated cereals, crackers made with saturated fats; pasta and rice dishes prepared in a butter cream sauce; baked, fried, mashed or scalloped potatoes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vegetables",
"       </td>",
"       <td>",
"        Any fresh or frozen vegetable such as artichoke and artichoke hearts, asparagus, beans (green, wax, Italian), bean sprouts, beets, broccoli, brussel sprouts, cabbage, carrots, cauliflower, celery, cucumber, eggplant, green onions, greens (collard, kale, mustard, turnip), mushrooms, okra, onions, pea pods, peppers, radishes, salad greens (endive, escarole, lettuce, romaine, spinach), sauerkraut, spinach, summer squash, tomatoes and tomato sauce, vegetable juices, water chestnuts, watercress, zucchini",
"       </td>",
"       <td>",
"        Any vegetables prepared in butter, cream, or cheese sauce; fried vegetables",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fruits",
"       </td>",
"       <td>",
"        Whole fresh and frozen fruits such as apple, banana, blueberries, cantaloupe, cherries, grapefruit, grapes, honeydew melon, kiwi, mango, orange, peach, pear, pineapple, plums, and strawberries",
"       </td>",
"       <td>",
"        Canned fruit, fruit juices, dried fruit and fried fruits",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Meat, poultry, fish, dried beans, eggs",
"       </td>",
"       <td>",
"        Fish, shellfish, skinned white-meat chicken and turkey, beans, peas, lentils, egg substitutes, egg whites, soybeans, and tofu",
"       </td>",
"       <td>",
"        Regular beef, pork, lamb, veal, and luncheon meats; fried chicken and fish, eggs, sausage, hot dogs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Milk, yogurt and cheese",
"       </td>",
"       <td>",
"        Skim and 1 percent milk, plain nonfat yogurt, nonfat yogurt sweetened with aspartame or nonnutritive sweetener; fat-free or low-fat cheese and cottage cheese",
"       </td>",
"       <td>",
"        2 percent and whole milk, 1 percent and whole chocolate milk, goat's milk, kefir, low-fat yogurt (plain or fruit flavored), custard-style yogurt, regular cheese and cottage cheese",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Texas Children's Hospital.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
