var title_f21_57_22428="Adolescent contraception tips";
var content_f21_57_22428=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F52699&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F52699&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Tips for increasing contraceptive adherence among adolescent females*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Contraceptive method",
"       </td>",
"       <td class=\"subtitle1\">",
"        Potential barriers to adherence",
"       </td>",
"       <td class=\"subtitle1\">",
"        Strategies to increase adherence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"5\">",
"        Oral contraceptive pills",
"       </td>",
"       <td rowspan=\"4\">",
"        <p>",
"         Failure to refill prescription",
"        </p>",
"        <p>",
"         Forgetting to take pill",
"        </p>",
"        <p>",
"         Using pills sporadically",
"        </p>",
"        <p>",
"         Not using a back-up method when necessary",
"        </p>",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Tell (and ask the adolescent to repeat back) three things:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - When to start the pill",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Take the pill every day at the same time (eg, when brushing teeth)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Call the clinic/office if there are any questions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Desire to avoid monthly periods",
"       </td>",
"       <td>",
"        Extended-cycle or continuous pill use",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Depot medroxyprogesterone acetate",
"       </td>",
"       <td>",
"        Failure to make/keep appointment for next shot",
"       </td>",
"       <td>",
"        Schedule the appointment for the next shot when the adolescent is in the clinic/office and ask her to set the alarm on her cell phone as a reminder",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Transdermal patch",
"       </td>",
"       <td>",
"        Requires weekly change",
"       </td>",
"       <td>",
"        <p>",
"         Use a wall, computer, or cell phone calendar",
"        </p>",
"        <p>",
"         Use a cell phone alarm",
"        </p>",
"        <p>",
"         Place a sticker designating the change day on the bathroom mirror",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Vaginal ring",
"       </td>",
"       <td>",
"        Requires change every three weeks",
"       </td>",
"       <td>",
"        <p>",
"         Use a wall, computer, or cell phone calendar",
"        </p>",
"        <p>",
"         Use a cell phone alarm",
"        </p>",
"        <p>",
"         Place a sticker designating the change day on the bathroom mirror",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Condoms",
"       </td>",
"       <td>",
"        Must be available at time of sexual activity",
"       </td>",
"       <td>",
"        <p>",
"         Provide condoms to the adolescent before she leaves the clinic/office",
"        </p>",
"        <p>",
"         Suggest that the adolescent carry condoms in her purse",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Provide the adolescent with a list of the following information: the clinic/office name, a contact person, the clinic/office phone number, and instructions about what to do if they miss a dose.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
