var title_f21_59_22459="Involving family and friends in pseudocyesis treatment";
var content_f21_59_22459=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F70027&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F70027&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Involving family and friends in treatment of pseudocyesis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Reducing negative influences from others",
"       </td>",
"       <td class=\"subtitle1\">",
"        Teaching others to assist with treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Educate others who support the patient's belief in pregnancy (eg, educate a boyfriend who insists patient must be pregnant because she has galactorrhea)",
"       </td>",
"       <td>",
"        Teach others how to encourage patient to accept treatment (eg, coaching spouse to explain that he is worried about patient and would like to come with her to see her doctor)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Help others reduce pressure on patient to become pregnant (eg, council the patient's mother who repeatedly tells patient that she is not a good wife unless she has a baby)",
"       </td>",
"       <td>",
"        Teach others how to respond therapeutically when patient says she is pregnant (eg, help them think of specific ways to show support apart from pregnancy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Help others reduce the secondary gain of pregnancy belief (eg, encourage family members to be solicitous of patient's needs without patient having to be \"pregnant\" to elicit that response from them)",
"       </td>",
"       <td>",
"        Improve communication (eg, help spouse communicate his frustrations directly and respectfully, instead of with avoidance)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
