var title_f21_59_22461="Molecular markers in AML";
var content_f21_59_22461=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F88091&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F88091&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Molecular markers in acute myeloid leukemia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Approximate frequency in de-novo AML",
"       </td>",
"       <td class=\"subtitle1\">",
"        Frequency in CN AML",
"       </td>",
"       <td class=\"subtitle1\">",
"        Strong associations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Not recorded with",
"       </td>",
"       <td class=\"subtitle1\">",
"        Outlook",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         NPM1",
"        </em>",
"       </td>",
"       <td>",
"        35 percent",
"       </td>",
"       <td>",
"        50 percent",
"       </td>",
"       <td>",
"        CN AML,",
"        <em>",
"         FLT3",
"        </em>",
"        -ITD,",
"        <em>",
"         FLT3",
"        </em>",
"        -TKD,",
"        <em>",
"         DNMT3A",
"        </em>",
"        ,",
"        <em>",
"         IDH1",
"        </em>",
"        ,",
"        <em>",
"         IDH2",
"        </em>",
"       </td>",
"       <td>",
"        <em>",
"         CEBPA",
"        </em>",
"        double mutant",
"       </td>",
"       <td>",
"        Favorable in patients with CN AML and in elderly patients",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         CEBPA",
"        </em>",
"       </td>",
"       <td>",
"        7 percent",
"       </td>",
"       <td>",
"        8 to 19 percent",
"       </td>",
"       <td>",
"        CN AML,",
"        <em>",
"         FLT3",
"        </em>",
"        -ITD",
"       </td>",
"       <td>",
"        <em>",
"         NPM1",
"        </em>",
"       </td>",
"       <td>",
"        Favorable in patients with CN AML (if biallelic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         FLT3",
"        </em>",
"        -ITD",
"       </td>",
"       <td>",
"        20 to 25 percent",
"       </td>",
"       <td>",
"        30 to 35 percent",
"       </td>",
"       <td>",
"        CN AML, APL, t(6;9),",
"        <em>",
"         NPM1",
"        </em>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Adverse in patients with CN AML; could vary with allelic burden; no clear effect in patients with APL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         FLT3",
"        </em>",
"        -TKD",
"       </td>",
"       <td>",
"        5 percent",
"       </td>",
"       <td>",
"        14 percent",
"       </td>",
"       <td>",
"        CN AML,",
"        <em>",
"         NPM1",
"        </em>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Controversial",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         KIT",
"        </em>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        25 percent",
"       </td>",
"       <td>",
"        Core binding factor leukemias",
"       </td>",
"       <td>",
"        Most other karyotypes",
"       </td>",
"       <td>",
"        Adverse in adult patients with core binding factor leukemias",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        TET2",
"       </td>",
"       <td>",
"        8 to 12 percent",
"       </td>",
"       <td>",
"        23 percent",
"       </td>",
"       <td>",
"        Possibly CN AML",
"       </td>",
"       <td>",
"        <em>",
"         IDH1",
"        </em>",
"        ,",
"        <em>",
"         IDH2",
"        </em>",
"       </td>",
"       <td>",
"        Controversial",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         DNMT3A",
"        </em>",
"       </td>",
"       <td>",
"        14 to 22 percent",
"       </td>",
"       <td>",
"        20 to 33 percent",
"       </td>",
"       <td>",
"        CN AML,",
"        <em>",
"         NPM1",
"        </em>",
"        ,",
"        <em>",
"         FLT3",
"        </em>",
"       </td>",
"       <td>",
"        Core binding factor leukemias,",
"        <em>",
"         CEBPA",
"        </em>",
"        ,",
"        <em>",
"         MLL",
"        </em>",
"        translocations",
"       </td>",
"       <td>",
"        Possibly adverse in patients with CN AML",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         IDH1",
"        </em>",
"        ,",
"        <em>",
"         IDH2",
"        </em>",
"       </td>",
"       <td>",
"        8 to 16 percent",
"       </td>",
"       <td>",
"        30 percent",
"       </td>",
"       <td>",
"        CN AML,",
"        <em>",
"         NPM1",
"        </em>",
"        ,",
"        <em>",
"         FLT3",
"        </em>",
"       </td>",
"       <td>",
"        <em>",
"         TET2",
"        </em>",
"        ,",
"        <em>",
"         WT1",
"        </em>",
"       </td>",
"       <td>",
"        Controversial",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <em>",
"         ASXL1",
"        </em>",
"       </td>",
"       <td>",
"        5 to 30 percent",
"       </td>",
"       <td>",
"        About 10 percent",
"       </td>",
"       <td>",
"        Uncommon with",
"        <em>",
"         NPM1",
"        </em>",
"        and",
"        <em>",
"         FLT3",
"        </em>",
"       </td>",
"       <td>",
"        Possibly",
"        <em>",
"         CEBPA",
"        </em>",
"       </td>",
"       <td>",
"        Adverse in patients with CN AML and older patients; more common in older patients",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Changes of miRNAs 15, 16, 155, and 181 among others, have been reported. The prognostic effect varies and studies are needed to assess the individual miRNA families and their interactions with other mutations. Some studies have shown that overexpression of BAALC, MN1, and ERG genes is associated with poorer outcomes, but needs confirmation.",
"    <div class=\"footnotes\">",
"     AML: acute myeloid leukemia; CN AML: cytogenetically normal acute myeloid leukemia; ITD: internal tandem duplication; TKD: tyrosine kinase domain; APL: acute promyelocytic leukemia.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Ferrara F, Schiffer CA. Acute myeloid leukaemia in adults. Lancet 2013; 381:484. Table used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
