" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Uterine leiomyoma (fibroid) embolization",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?21/60/22473/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?21/60/22473/contributors\">",
